//ClassOrInterfaceDeclaration/ClassOrInterfaceBody
    [
     count(./ClassOrInterfaceBodyDeclaration/MethodDeclaration/MethodDeclarator[
        not (
               starts-with(@Image,'get')
               or
               starts-with(@Image,'set')
               or
               starts-with(@Image,'is')
           )
     ]) > $maxmethods
  ]
